css:
.dashboard-section {

}
.dashboard-section > button {
margin-left: 10px;
}
.barcode-label-container {
float: left;
width: fit-content;
overflow: hidden;
}
.barcode-label {
background-color: #ffffff;
color: #000000;
box-sizing: border-box;
height: 28.95mm;
width: 51.5mm;
padding: 2.5mm;
padding-left: 5mm;
padding-right: 3.5mm;
padding-top: 5mm;
display: grid;
grid-template-columns: 1fr 16mm;
grid-template-rows: 4mm 1fr 10.5mm;
grid-template-areas: "header header" "text footer" "barcode image";
font-family: monospace;
font-size: 6pt;
line-height: 1.2;
text-align: center;
}
.barcode-rotated {
line-height: 0.8mm;
height: 6mm;
}
.barcode-header {
grid-area: header;
text-align: left;
}
.barcode-text {
grid-area: text;
height: 40px;
overflow-y: hidden;
font-size: 5pt;
text-align: left;
width: 29mm;
}
.barcode-image {
grid-area: image;
overflow-y: hidden;
align-self: center;
}
.barcode-im {
grid-area: image;
overflow-y: hidden;
width: 8mm;
margin-right: 9px;
}
.barcode-icon {
grid-area: image;
overflow-y: hidden;
width: 4mm;
}
.barcode-area {
grid-area: barcode;
align-self: center;
width: 14mm;
}
.barcode-foot {
grid-area: footer;
font-size: 3pt;
text-align: left;
}
.barcode-strong {
}
@media screen {
    .print-format {
        padding: 0.0in;
    }
}
.barcode {
width: 107px;
height: 60px;
x: 0px;
y: 0px;
}
